#include <gtest/gtest.h>

#include "fintamath/core/Cache.hpp"
#include "fintamath/numbers/RealFunctions.hpp"

#include "fintamath/exceptions/UndefinedException.hpp"
#include "fintamath/numbers/IntegerFunctions.hpp"

using namespace fintamath;

namespace {

unsigned precision = 500;

const Real &getBottom() {
  static const Real res = pow(Rational("9.99"), precision);
  return res;
}

const Real &getTop() {
  static const Real res = pow(Rational("10.01"), precision);
  return res;
}

const Real &getLogBottom() {
  static const Real res = pow(precision - 1, getE());
  return res;
}

const Real &getLogTop() {
  static const Real res = pow(precision + 1, getE());
  return res;
}

}

TEST(RealFunctionsTests, floorTest) {
  Real::ScopedSetPrecision setPrecision(precision);

  EXPECT_EQ(floor(Real("-1000.8")).toString(),
            "-1001");
  EXPECT_EQ(floor(Real("-1000.1")).toString(),
            "-1001");
  EXPECT_EQ(floor(Real("-1000")).toString(),
            "-1000");
  EXPECT_EQ(floor(Real("-1.9")).toString(),
            "-2");
  EXPECT_EQ(floor(Real("-1.5")).toString(),
            "-2");
  EXPECT_EQ(floor(Real("-1.3")).toString(),
            "-2");
  EXPECT_EQ(floor(Real("-1")).toString(),
            "-1");
  EXPECT_EQ(floor(Real("-0.9")).toString(),
            "-1");
  EXPECT_EQ(floor(Real("-0.3")).toString(),
            "-1");
  EXPECT_EQ(floor(Real("0")).toString(),
            "0");
  EXPECT_EQ(floor(Real("0.3")).toString(),
            "0");
  EXPECT_EQ(floor(Real("0.9")).toString(),
            "0");
  EXPECT_EQ(floor(Real("1")).toString(),
            "1");
  EXPECT_EQ(floor(Real("1.9")).toString(),
            "1");
  EXPECT_EQ(floor(Real("1.5")).toString(),
            "1");
  EXPECT_EQ(floor(Real("1.3")).toString(),
            "1");
  EXPECT_EQ(floor(Real("1000")).toString(),
            "1000");
  EXPECT_EQ(floor(Real("1000.1")).toString(),
            "1000");
  EXPECT_EQ(floor(Real("1000.8")).toString(),
            "1000");

  EXPECT_EQ(floor(getBottom()).toString(),
            "");
  EXPECT_EQ(floor(-getBottom()).toString(),
            "-");
  EXPECT_EQ(floor(1 / getBottom()).toString(),
            "0");
  EXPECT_EQ(floor(-1 / getBottom()).toString(),
            "-1");
  EXPECT_EQ(floor(1 + 1 / getBottom()).toString(),
            "1");
  EXPECT_EQ(floor(1 - 1 / getBottom()).toString(),
            "0");
  EXPECT_EQ(floor(-1 + 1 / getBottom()).toString(),
            "-1");
  EXPECT_EQ(floor(-1 - 1 / getBottom()).toString(),
            "-2");

  EXPECT_THROW(floor(getTop()),
               UndefinedFunctionException);
  EXPECT_THROW(floor(-getTop()),
               UndefinedFunctionException);
  EXPECT_EQ(floor(1 / getTop()).toString(),
            "0");
  EXPECT_EQ(floor(-1 / getTop()).toString(),
            "-1");
  EXPECT_EQ(floor(1 + 1 / getTop()).toString(),
            "1");
  EXPECT_EQ(floor(1 - 1 / getTop()).toString(),
            "0");
  EXPECT_EQ(floor(-1 + 1 / getTop()).toString(),
            "-1");
  EXPECT_EQ(floor(-1 - 1 / getTop()).toString(),
            "-2");
}

