TEST(RealFunctionsTests, powTest) {
  Real::ScopedSetPrecision setPrecision(precision);

  EXPECT_EQ(pow(Real("0"), Real("1")).toString(),
            "0.0");
  EXPECT_EQ(pow(Real("1"), Real("0")).toString(),
            "1.0");
  EXPECT_EQ(pow(Real("1"), Real("1")).toString(),
            "1.0");
  EXPECT_EQ(pow(Real("-1"), Real("0")).toString(),
            "1.0");
  EXPECT_EQ(pow(Real("-1"), Real("-1")).toString(),
            "-1.0");
  EXPECT_EQ(pow(Real("10"), Real("0")).toString(),
            "1.0");
  EXPECT_EQ(pow(Real("-10"), Real("3")).toString(),
            "-1000.0");
  EXPECT_EQ(pow(Real("1"), Real("25")).toString(),
            "1.0");
  EXPECT_EQ(pow(Real("2.2"), Real("5")).toString(),
            "51.53632");
  EXPECT_EQ(pow(Real("10"), Real("10000")).toString(),
            "1.0*10^10000");
  EXPECT_EQ(pow(Real("20"), Real("10000")).toString(),
            "1.*10^13010");
  EXPECT_EQ(pow(Real("10"), Real("-10000")).toString(),
            "1.0*10^-10000");
  EXPECT_EQ(pow(Real("-10"), Real("-3")).toString(),
            "-0.001");
  EXPECT_EQ(pow(Real("-1"), Real("-25")).toString(),
            "-1.0");
  EXPECT_EQ(pow(Real("-2.2"), Real("-5")).toString(),
            "-0.");
  EXPECT_EQ(pow(Real("-10"), Real("-10000")).toString(),
            "1.0*10^-10000");
  EXPECT_EQ(pow(Real("-20"), Real("-10000")).toString(),
            "5.*10^-13011");

  EXPECT_EQ(pow(Real("10"), 1 / Real("3")).toString(),
            "2.");
  EXPECT_EQ(pow(Real("1"), 2 / Real("25")).toString(),
            "1.0");
  EXPECT_EQ(pow(Real("2.2"), 3 / Real("5")).toString(),
            "1.");
  EXPECT_EQ(pow(Real("1.61051"), Real("0.2")).toString(),
            "1.1");
  EXPECT_EQ(pow(Real("10"), Real("12527") / Real("10000")).toString(),
            "17.");
  EXPECT_EQ(pow(Real("3208912742835798256297875237432984"), Real("2873872") / Real("32864198276498721360325792813562983746832458643")).toString(),
            "1.");
  EXPECT_EQ(pow(Real("20"), Real("123.123")).toString(),
            "15371496698710127538049660207002996251046701240373832293320415676548540249323541328683199199947267490554116434553064075939129183501326770706172585764242164485464.");
  EXPECT_EQ(pow(Real("10"), 1 / Real("-10000")).toString(),
            "0.");

  EXPECT_THROW(pow(Real("0"), Real("0")),
               UndefinedBinaryOperatorException);
  EXPECT_THROW(pow(Real("0"), Real("-0")),
               UndefinedBinaryOperatorException);
  EXPECT_THROW(pow(Real("-0"), Real("0")),
               UndefinedBinaryOperatorException);
  EXPECT_THROW(pow(Real("-0"), Real("-0")),
               UndefinedBinaryOperatorException);
  EXPECT_THROW(pow(Real("0"), Real("-10")),
               UndefinedBinaryOperatorException);
  EXPECT_THROW(pow(Real("-10"), Real("1.5")),
               UndefinedBinaryOperatorException);
  EXPECT_THROW(pow(Real("-0"), Real("1.5")),
               UndefinedBinaryOperatorException);
  EXPECT_THROW(pow(Real("10"), Real("100000000000000000000")),
               UndefinedBinaryOperatorException);

  EXPECT_THROW(pow(2, getBottom()),
               UndefinedBinaryOperatorException);
  EXPECT_EQ(pow(2, -getBottom()).toString(),
            "0.0");
  EXPECT_EQ(pow(2, 1 / getBottom()).toString(),
            "1.0");
  EXPECT_EQ(pow(2, -1 / getBottom()).toString(),
            "0.");
  EXPECT_EQ(pow(2, 1 + 1 / getBottom()).toString(),
            "2.0");
  EXPECT_EQ(pow(2, 1 - 1 / getBottom()).toString(),
            "2.0");
  EXPECT_EQ(pow(2, -1 + 1 / getBottom()).toString(),
            "0.");
  EXPECT_EQ(pow(2, -1 - 1 / getBottom()).toString(),
            "0.");

  EXPECT_THROW(pow(2, getTop()),
               UndefinedBinaryOperatorException);
  EXPECT_EQ(pow(2, -getTop()).toString(),
            "0.0");
  EXPECT_EQ(pow(2, 1 / getTop()).toString(),
            "1.0");
  EXPECT_EQ(pow(2, -1 / getTop()).toString(),
            "1.0");
  EXPECT_EQ(pow(2, 1 + 1 / getTop()).toString(),
            "2.0");
  EXPECT_EQ(pow(2, 1 - 1 / getTop()).toString(),
            "2.0");
  EXPECT_EQ(pow(2, -1 + 1 / getTop()).toString(),
            "0.5");
  EXPECT_EQ(pow(2, -1 - 1 / getTop()).toString(),
            "0.5");
}

